import React, { useCallback, useEffect, useState } from 'react'
import styled from 'styled-components'
import { useDispatch, useSelector } from 'react-redux'
import { PostsSelectors } from '@Features/Posts'
import {ListPosts} from '../Molecules'
import { PostType } from '@API/REST'
import { CreatePostForm } from '@UI/Organisms'
import { Button } from '@UI/Atoms'

const Wrapped = styled.div``
const PostsWrapped = styled.div`
	margin: 30px 0;
`
interface PropsType {
	posts: PostType[]
}
export const PostsActions: React.FC<PropsType> = ({ posts }): React.ReactElement => {
	const [isOpenCreatePost, setIsOpenCreatePost] = useState(false)
	const test = useSelector(PostsSelectors.posts)
	useEffect(() => {
		console.log(test)
	}, [test])
	console.log(test)
	const dispatch = useDispatch()
	const onSubmit = useCallback(
		(value: { title: string; body: string }) => {
			dispatch({ type: 'GET_POSTS' })
			setIsOpenCreatePost(!isOpenCreatePost)
		},
		[dispatch, isOpenCreatePost]
	)
	return (
		<Wrapped>
			<Button
				onClick={() => {
					setIsOpenCreatePost(!isOpenCreatePost)
				}}
			>
				Create
			</Button>
			<PostsWrapped>
				<ListPosts posts={posts} />
			</PostsWrapped>
			{isOpenCreatePost && <CreatePostForm onSubmit={onSubmit} />}
		</Wrapped>
	)
}
