import { PostType } from '@API/REST'
import { ActionsType } from './actions'
import * as types from './types'
import { HYDRATE } from 'next-redux-wrapper'

export const initialValue = {
	isLoading: false,
	posts: null as PostType[] | null,
}

type InitialValueType = typeof initialValue
export const reducer = (state = initialValue, action: ActionsType): InitialValueType => {
	switch (action.type) {
		case HYDRATE:
			const stateDiff = diff(state, action.payload) as any;
			const wasBumpedOnClient = stateDiff?.page?.[0]?.endsWith('X'); // or any other criteria
			return {
				...state,
				...action.payload,
				page: wasBumpedOnClient ? state.page : action.payload.page, // keep existing state or use hydrated
			};
		case types.SET_LOADING: {
			return {
				...state,
				isLoading: action.payload,
			}
		}
		case types.SET_POSTS: {
			return {
				...state,
				posts: action.payload,
			}
		}
		default:
			return state
	}
}
