import { createInstance } from '@API/api'

interface PostsType {
	id: number
	title: string
	body: string
}
interface PayloadType {
	postId: number
	body: string
}
export const createComments = (payload: PayloadType) => {
	return createInstance().post<PostsType>('/comments', { payload })
}
