import { applyMiddleware, createStore } from 'redux'
import { composeWithDevTools } from 'redux-devtools-extension'
import createSagaMiddleware, { Task } from 'redux-saga'

import { createWrapper, MakeStore, Context } from 'next-redux-wrapper'
import { createRouterMiddleware } from 'connected-next-router'
import { rootReducer, AppStateType } from './root-reducer'
import { rootSaga } from './root-saga'

const makeStore: MakeStore<PostsInitialValue> = (context: Context) => {
	const sagaMiddleware = createSagaMiddleware()
	const store = createStore(
		rootReducer,
		composeWithDevTools(applyMiddleware(sagaMiddleware))
	)

	//@ts-ignore
	store.sagaTask = sagaMiddleware.run(rootSaga)
	return store
}

// export an assembled wrapper
export const wrapper = createWrapper<AppStateType>(makeStore, { debug: true })

type PropertiesType<T> = T extends { [key: string]: infer U } ? U : never
export type InferActionsType<
	T extends { [key: string]: (...args: any[]) => any }
> = ReturnType<PropertiesType<T>>
