import { PostType } from '@API/REST'
import { API } from '@API/index'
import { useRouter } from 'next/router'
import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { PostsSelectors } from '@Features/Posts'

const Post = (): React.ReactElement => {
	const isLoading = useSelector(PostsSelectors.isLoading)
	const dispatch = useDispatch()
	const {
		query: { id },
	} = useRouter()

	useEffect(() => {
		dispatch({ type: 'VIEW_POST', payload: { id } })
	}, [dispatch, id])
	return !isLoading ? <div>Post {id}</div> : <Preloader />
}

export default Post
