import React, { useCallback } from 'react'
import styled from 'styled-components'
import { useDispatch } from 'react-redux'
import { Button } from 'antd'
import { PostInformationType } from '@API/REST'
import { PostInformation } from '@UI/Organisms'
import { CommentItem } from '@UI/Atoms'
import { Actions } from '../Molecules'

const Wrapped = styled.div`
	width: 550px;
	margi: 0 auto;
`
const Information = styled.div``
const PositionWrapped = styled.div`
	display: grid;
	align-items: center;
`
interface PropsType {
	post: PostInformationType
}
export const Post: React.FC<PropsType> = ({ post }): React.ReactElement => {
	const { id, title, body, comments } = post
	const dispatch = useDispatch()
	const onEdit = useCallback(() => {
		dispatch({ type: 'EDIT_POST', payload: { id } })
	}, [dispatch, id])
	return (
		<PositionWrapped>

		<Wrapped>
			<Actions id={id}>
				<Button onClick={onEdit}>Edit</Button>
			</Actions>
			<Information>
				<PostInformation title={title} body={body}>
					<>
						{comments.map(({ body, id }) => {
							return <CommentItem key={id} body={body} />
						})}
					</>
				</PostInformation>
			</Information>
		</Wrapped>
		</PositionWrapped>
	)
}
