import { call, put, all, takeEvery } from 'redux-saga/effects'
import { actions } from '@Features/Posts/Model/actions'
import { API, PostType } from '@API/index'
import * as types from './types'

function* getPostsSaga() {
	yield put(actions.setLoading(true))
	try {
		const response = yield call(API.PostsAPI.getPosts)
		yield put(actions.setPosts(response.data))
		yield put(actions.setLoading(false))
	} catch (error) {
		yield put(actions.setLoading(false))
		console.log(error)
	}
}

interface CreatePost {
	type: string
	payload: PostType
}
function* createPostSaga({ payload }: CreatePost) {
	yield put(actions.setLoading(true))
	try {
		const response = yield call(API.PostsAPI.createPost, payload)
		const postsResponse = yield call(API.PostsAPI.getPosts)
		yield put(actions.setPosts(response.data))
		yield put(actions.setLoading(false))
	} catch (error) {
		yield put(actions.setLoading(false))
		console.log(error)
	}
}
export function* rootSaga() {
	yield all([
		takeEvery(types.GET_POSTS, getPostsSaga),
		takeEvery(types.CREATE_POST, createPostSaga),
	])
}
