// }

const MyApp: React.FC<AppProps> = ({ Component, pageProps }) => {
	return (
		<Provider store={store}>
			<ThemeProvider theme={theme}>
				<MainTemplate>
					<Component {...pageProps} />
				</MainTemplate>
			</ThemeProvider>
		</Provider>
	)
}

export default wrapper.withRedux(MyApp)
