import { applyMiddleware, combineReducers, compose, createStore, Store } from 'redux'
import { composeWithDevTools } from 'redux-devtools-extension'
import createSagaMiddleware, { Task } from 'redux-saga'
import { createWrapper, MakeStore } from 'next-redux-wrapper'
import { AppStateType, rootReducer } from './root-reducer'
import { rootSaga } from './root-saga'

declare global {
	interface Window {
		__REDUX_DEVTOOLS_EXTENSION__: Function
		__REDUX_DEVTOOLS_EXTENSION_COMPOSE__?: Function
	}
}

function configureReducer<S>(isServer: boolean, rootReducer: Reducer<S>): Reducer<S> {
	if (!isServer) {
		return persistReducer(
			{
				key: 'root',
				version: 0,
				storage: localforage,
				whitelist: [],
			},
			reduceCompoundActions(rootReducer)
		)
	}

	return reduceCompoundActions(rootReducer)
}

const composeEnhancers =
	(typeof window !== 'undefined' && window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__) ||
	compose

interface MakeStoreOpts {
	isServer: boolean
}

export function configureStore(stateFromServer: AppState, options: MakeStoreOpts) {
	const sagaMiddleware = createSagaMiddleware()

	const store = createStore(
		configureReducer(options.isServer, reducers),
		stateFromServer || initialState,
		composeEnhancers(applyMiddleware(sagaMiddleware))
	)

	// redux persistence to local storage
	;(store as any).__persister = persistStore(store)

	// start saga middleware
	;(store as any).sagaTask = sagaMiddleware.run(rootSaga)

	return store
}
type PropertiesType<T> = T extends { [key: string]: infer U } ? U : never
export type InferActionsType<
	T extends { [key: string]: (...args: any[]) => any }
> = ReturnType<PropertiesType<T>>

export interface SagaStore extends Store {
	sagaTask?: Task
}
