import { AxiosResponse } from 'axios'
import { createInstance } from '@API/api'

interface PostsType {
	id: number
	title: string
	body: string
}
interface PayloadType {
	title: string
	body: string
}
export const createPost = (payload: PayloadType): Promise<AxiosResponse<PostsType>> => {
	return createInstance().post<PostsType>('/posts', { payload })
}
